methods.onEnterWaitForGreeting:
async function(lifecycle, payload, fsm, makeExternalApiCall, axios, doAriAction) {
        console.log("ARI FSM: Entered waitForGreeting for channel " + fsm.channelId + ". Playing welcome sound.");
        try {
            await doAriAction("playSound", fsm, { media: "sound:hello-world" });
            fsm.greetingPlayed();
        } catch (err) { console.error("ARI FSM: Error playing sound:", err); fsm.hangupCall(); }
    }

methods.onEnterHandleOption1:
async function(lifecycle, payload, fsm, makeExternalApiCall, axios, doAriAction) {
        console.log("ARI FSM: Handling DTMF option 1.");
        try {
            await doAriAction("playSound", fsm, { sound: "digits/1" });
            await new Promise(resolve => setTimeout(resolve, 500));
            fsm.hangupCall();
        } catch (err) { console.error("ARI FSM: Error in handleOption1:", err); fsm.hangupCall(); }
    }

methods.onBeforeHangupCall:
async function(lifecycle, payload, fsm, makeExternalApiCall, axios, doAriAction) {
        console.log("ARI FSM: Hanging up call on channel " + fsm.channelId);
        try { await doAriAction("hangup", fsm); } catch (err) { console.error("ARI FSM: Error during hangup:", err); }
    }
